/// <summary>
/// The <c>SysMessageButtonViewModel</c> class controls forms buttons used by message processor forms.
/// </summary>
public abstract class SysMessageButtonViewModel
{
    FormFunctionButtonControl       button;

}

Method.Source:
/// <summary>
/// Initializes the instance parameters.
/// </summary>
/// <param name = "_messageDataSource">A message form data source.</param>
/// <param name = "_button">A button.</param>
[Hookable(false)]
protected void initialize(
    FormDataSource            _messageDataSource,
    FormFunctionButtonControl _button)
{
    this.button = _button;

    if (_messageDataSource)
    {
        _messageDataSource.OnSelectionChanged += eventhandler(this.onMessageDataSourceSelectionChanged);
        _messageDataSource.OnMarkChanged += eventhandler(this.onMessageDataSourceMarkChanged);
    }
}

/// <summary>
/// Determines if the button should be enabled.
/// </summary>
/// <param name = "_formDataSource">The form data source.</param>
/// <returns>true if the button should be enabled; otherwise, false.</returns>
public abstract boolean enableButton(FormDataSource _formDataSource)
{
}

[SuppressBpWarning('BPParameterNotUsed', 'Parameter is required by the implemented handler signature.')]
private void onMessageDataSourceSelectionChanged(FormDataSource _sender, FormDataSourceEventArgs _e)
{
    if (button)
    {
        button.enabled(this.enableButton(_sender));
    }
}

[SuppressBpWarning('BPParameterNotUsed', 'Parameter is required by the implemented handler signature.')]
private void onMessageDataSourceMarkChanged(FormDataSource _sender, FormDataSourceEventArgs _e)
{
    if (button)
    {
        button.enabled(this.enableButton(_sender));
    }
}
